partials.ajax_chip:
{{#with (parse (@partial-block))}}
  <div class="mdc-chip {{if ../on "mdc-chip--selected"}}" {{{extraAttributes}}} data-ajax-chip='{
      "onUrl": "{{../onUrl}}",
      "offUrl": "{{../offUrl}}",
      "onMethod": "{{default ../onMethod "GET"}}",
      "offMethod": "{{default ../offMethod "GET"}}",
      {{#if onData}}
        "onData": {{{stringify onData}}},
      {{/if}}
      {{#if offData}}
        "offData": {{{stringify offData}}},
      {{/if}}
      {{#if onClasses}}
      "onClasses": {{{stringify onClasses}}},
      {{/if}}
      {{#if offClasses}}
      "offClasses": {{{stringify offClasses}}},
      {{/if}}
      "onText": "{{../onText}}",
      "offText": "{{../offText}}"
    }'>
    <span class="{{../iconClasses}} mdc-chip__icon mdc-chip__icon--leading {{if ../on "mdc-chip__icon--leading-hidden"}}" aria-hidden="true"></span>
    <div class="mdc-chip__checkmark">
      <svg class="mdc-chip__checkmark-svg" viewBox="-2 -3 30 30">
        <path class="mdc-chip__checkmark-path" fill="none" stroke="#ffffff" d="M1.73,12.91 8.1,19.28 22.79,4.59"/>
      </svg>
    </div>
    <div class="mdc-chip__text noselect">{{if ../on ../onText else=../offText}}</div>
  </div>
{{/with}}

partials.banner_ad:
{{#compare @root.data.supplimentaryResponses.banner.hits.total ">" 0 ~}}
{{#with @root.data.supplimentaryResponses.banner.hits.hits.[0]._source ~}}
<div class="x-items">
  <a href="{{url}}" class="x-item mdc-theme--secondary-bg mdc-elevation--z1" target="_blank">
    <img class="responsive-img" src="{{og_image}}" alt="{{title}}" height="90">
  </a>
</div>
{{/with ~}}
{{/compare ~}}

partials.browse:
{{#compare @root.data.supplimentaryResponses.types.hits.total ">" 0 ~}}
<div class="mdc-layout-grid home-content-primary">
  <div class="mdc-layout-grid__inner">
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone content page-content">
      <div class="browse-title browse-title-roles mdc-typography--headline5">
        Roles
      </div>
      <ul class="mdc-list">
        {{#each @root.data.supplimentaryResponses.types.aggregations.roles.buckets ~}}
          {{#if key ~}}
            <a class="mdc-list-item" href="/search?roles%5B%5D={{urlencode (slugify (replace key "/" "-"))}}" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default ../eventContext "category-browser"}}', event_label: 'Role: {{slugify (replace key "/" "-")}}' })">
              <span class="mdc-list-item__text">
                {{{key}}}
              </span>
              <span class="mdc-list-item__meta" aria-label="{{doc_count}} vacancies">
                {{doc_count}}
              </span>
            </a>
          {{/if ~}}
        {{/each ~}}
      </ul>

      <div class="browse-title browse-title-working-patterns mdc-typography--headline5">
        Working patterns
      </div>
      <ul class="mdc-list">
        {{#each @root.data.supplimentaryResponses.types.aggregations.working_patterns.buckets ~}}
          {{#if key ~}}
            <a class="mdc-list-item" href="/search?working_patterns%5B%5D={{urlencode (slugify (replace key "/" "-"))}}" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default ../eventContext "category-browser"}}', event_label: 'Working pattern: {{slugify (replace key "/" "-")}}' })">
              <span class="mdc-list-item__text">
                {{{key}}}
              </span>
              <span class="mdc-list-item__meta" aria-label="{{doc_count}} vacancies">
                {{doc_count}}
              </span>
            </a>
          {{/if ~}}
        {{/each ~}}
      </ul>
    </div>
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone content page-content">
      <div class="browse-title browse-title-sectors mdc-typography--headline5">
        Sectors
      </div>
      <ul class="mdc-list">
        {{#each @root.data.supplimentaryResponses.types.aggregations.sectors.buckets ~}}
          {{#if key ~}}
            <a class="mdc-list-item" href="/search?sectors%5B%5D={{urlencode (slugify (replace key "/" "-"))}}" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default ../eventContext "category-browser"}}', event_label: 'Sector: {{slugify (replace key "/" "-")}}' })">
              <span class="mdc-list-item__text">
                {{{key}}}
              </span>
              <span class="mdc-list-item__meta" aria-label="{{doc_count}} vacancies">
                {{doc_count}}
              </span>
            </a>
          {{/if ~}}
        {{/each ~}}
      </ul>
    </div>
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone content page-content">
      <div class="browse-title browse-title-regions mdc-typography--headline5">
        Regions
      </div>
      <ul class="mdc-list">
        {{#compare (length @root.data.supplimentaryResponses.types_regions.aggregations.regions.buckets) ">" 0 ~}}
        <li class="mdc-list-item mdc-list-item--0">
          <h3 class="mdc-typography--headline7">Scotland</h3>
        </li>
        {{#each @root.data.supplimentaryResponses.types_regions.aggregations.regions.buckets ~}}
          {{#if key ~}}
            <a class="mdc-list-item" href="/search?regions%5B%5D={{urlencode (slugify (replace key "/" "-"))}}" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default ../../eventContext "category-browser"}}', event_label: 'Region: {{slugify (replace key "/" "-")}}' })">
              <span class="mdc-list-item__text">
                {{{key}}}
              </span>
              <span class="mdc-list-item__meta" aria-label="{{doc_count}} vacancies">
                {{doc_count}}
              </span>
            </a>
          {{/if ~}}
        {{/each ~}}
        {{/compare ~}}
        {{#compare (length @root.data.supplimentaryResponses.types_regions_england.aggregations.regions.buckets) ">" 0 ~}}
        <li class="mdc-list-item mdc-list-item--1">
          <h3 class="mdc-typography--headline7">England</h3>
        </li>
        {{#each @root.data.supplimentaryResponses.types_regions_england.aggregations.regions.buckets ~}}
          {{#if key ~}}
            <a class="mdc-list-item" href="/search?regions%5B%5D={{urlencode (slugify (replace key "/" "-"))}}" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default ../../eventContext "category-browser"}}', event_label: 'Region: {{slugify (replace key "/" "-")}}' })">
              <span class="mdc-list-item__text">
                {{{key}}}
              </span>
              <span class="mdc-list-item__meta" aria-label="{{doc_count}} vacancies">
                {{doc_count}}
              </span>
            </a>
          {{/if ~}}
        {{/each ~}}
        {{/compare ~}}
        {{#compare (length @root.data.supplimentaryResponses.types_regions_northern_ireland.aggregations.regions.buckets) ">" 0 ~}}
        <li class="mdc-list-item mdc-list-item--2">
          <h3 class="mdc-typography--headline7">Northern Ireland</h3>
        </li>
        {{#each @root.data.supplimentaryResponses.types_regions_northern_ireland.aggregations.regions.buckets ~}}
          {{#if key ~}}
            <a class="mdc-list-item" href="/search?regions%5B%5D={{urlencode (slugify (replace key "/" "-"))}}" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default ../../eventContext "category-browser"}}', event_label: 'Region: {{slugify (replace key "/" "-")}}' })">
              <span class="mdc-list-item__text">
                {{{key}}}
              </span>
              <span class="mdc-list-item__meta" aria-label="{{doc_count}} vacancies">
                {{doc_count}}
              </span>
            </a>
          {{/if ~}}
        {{/each ~}}
        {{/compare ~}}
        {{#compare (length @root.data.supplimentaryResponses.types_regions_wales.aggregations.regions.buckets) ">" 0 ~}}
        <li class="mdc-list-item mdc-list-item--3">
          <h3 class="mdc-typography--headline7">Wales</h3>
        </li>
        {{#each @root.data.supplimentaryResponses.types_regions_wales.aggregations.regions.buckets ~}}
          {{#if key ~}}
            <a class="mdc-list-item" href="/search?regions%5B%5D={{urlencode (slugify (replace key "/" "-"))}}" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default ../../eventContext "category-browser"}}', event_label: 'Region: {{slugify (replace key "/" "-")}}' })">
              <span class="mdc-list-item__text">
                {{{key}}}
              </span>
              <span class="mdc-list-item__meta" aria-label="{{doc_count}} vacancies">
                {{doc_count}}
              </span>
            </a>
          {{/if ~}}
        {{/each ~}}
        {{/compare ~}}

        {{#ifAny (length @root.data.supplimentaryResponses.types.aggregations.home_based.buckets) (length @root.data.supplimentaryResponses.types.aggregations.various_locations.buckets) ~}}
          <li class="mdc-list-item mdc-list-item--other">
            <h3 class="mdc-typography--headline7">Other</h3>
          </li>
          {{#compare (length @root.data.supplimentaryResponses.types.aggregations.various_locations.buckets) ">=" 1 ~}}
            <a class="mdc-list-item" href="/search?various_locations%5B%5D=true" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default eventContext "category-browser"}}', event_label: 'Various locations' })">
              <span class="mdc-list-item__text">
                Various locations
              </span>
              <span class="mdc-list-item__meta" aria-label="{{@root.data.supplimentaryResponses.types.aggregations.various_locations.buckets.0.doc_count}} vacancies">
                {{@root.data.supplimentaryResponses.types.aggregations.various_locations.buckets.0.doc_count}}
              </span>
            </a>
          {{/compare ~}}
          {{#compare (length @root.data.supplimentaryResponses.types.aggregations.home_based.buckets) ">=" 1 ~}}
            <a class="mdc-list-item" href="/search?home_based%5B%5D=true" role="menuitem" onclick="gtag('event', 'navigation', { event_category: '{{default eventContext "category-browser"}}', event_label: 'Home-based' })">
              <span class="mdc-list-item__text">
                Home-based
              </span>
              <span class="mdc-list-item__meta" aria-label="{{@root.data.supplimentaryResponses.types.aggregations.home_based.buckets.0.doc_count}} vacancies">
                {{@root.data.supplimentaryResponses.types.aggregations.home_based.buckets.0.doc_count}}
              </span>
            </a>
          {{/compare ~}}
        {{/ifAny ~}}
      </ul>
    </div>
  </div>
</div>
{{else}}
<div class="home-content-primary">
  <div class="content page-content">
    <div class="browse-title mdc-typography--headline5">
      No jobs are currently listed
    </div>
  </div>
</div>
{{/compare ~}}

partials.components_anchored_menu:
{{#with (default menuId (concat "menu-" (random 1000 9999))) as |menuId|}}
<div class="mdc-menu-surface--anchor {{../anchorClasses}}">
  <button class="mdc-button {{../buttonClasses}}" id="{{menuId}}-trigger">
    {{#if ../imageUrl ~}}
      <img src="{{../imageUrl}}" class="{{../imageClasses}}" />
    {{/if}}
    {{#if ../iconClasses}}
      <span class="{{../iconClasses}}"></span>
    {{/if ~}}
    {{{../buttonLabel}}}
    {{#if ../arrowClasses}}
      <span class="{{../arrowClasses}}"></span>
    {{/if}}
  </button>
  <div class="mdc-menu mdc-menu-surface {{../menuClasses}}" tabindex="-1" id="{{menuId}}" data-menu-trigger="#{{menuId}}-trigger">
    <ul class="mdc-menu__items mdc-list {{../menuListClasses}}" role="menu" aria-hidden="true">
      {{> @partial-block}}
    </ul>
  </div>
</div>
{{/with}}

partials.components_horizontal_card_scroller:
{{#ifAll (compare (length items) "===" 0) @partial-block}}
  {{> @partial-block}}
{{else}}
  <div class="mdc-layout-grid equal-height {{outerClasses}}">
    {{#if (any items)}}
    <div class="scroll-button scroll-left {{scrollButtonClasses}} {{scrollButtonLeftClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollLeftIconClasses}}"></span>
    </div>
    <div class="scroll-button scroll-right {{scrollButtonClasses}} {{scrollButtonRightClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollRightIconClasses}}"></span>
    </div>
    {{/if}}
    <div class="mdc-layout-grid__inner scrolling-grid {{innerClasses}}">
      {{#if (any items)}}
        {{#each items}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone {{../itemClasses}}">
            {{> (lookup ../this 'itemsPartial')}}
          </div>
        {{/each}}
        {{#if moreUrl}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone latest-content-primary last-card {{moreClasses}}">
            <div class="mdc-card align-centre mdc-typography--body1 {{moreCardClasses}}">
              <a href="{{{moreUrl}}}">{{default moreText "More"}}</a>
            </div>
          </div>
        {{/if}}
      {{/if}}
    </div>
  </div>
{{/ifAll}}

partials.email_frequency:
{{#if @root.data.auth ~}}
  {{#with @root.data.searches.items.[0].[0] ~}}
    {{#>components_radio_button
        id="daily"
        name="frequency"
        label="Daily"
        active=(compare days "1234567")~}}
      {
        "url": "/update-email-preferences",
        "method": "POST",
        "successMessage": "You will now recieve an email every day if there are new jobs matching your preferences",
        "failureMessage": "Failed to update email frequency setting to daily",
        "postBody": {
          {{~#each (split "sunday,monday,tuesday,wednesday,thursday,friday,saturday" ",")~}}
            "{{this}}": true
            {{~#unless @last}},{{/unless~}}
          {{~/each~}}
        }
      }
    {{~/components_radio_button}}
    {{#>components_radio_button
        id="weekly"
        name="frequency"
        label="Weekly"
        active=(compare (length days) 1)~}}
      {
        "url": "/update-email-preferences",
        "method": "POST",
        "successMessage": "You will now recieve an email every every {{momentFormat (moment (dateMath "now+1d") "x") "dddd"}} (starting tomorrow) if there are new jobs matching your preferences",
        "failureMessage": "Failed to update email frequency setting to weekly",
        "postBody": {
          "{{lowercase (momentFormat (moment (dateMath "now+1d") "x") "dddd")}}": true
        }
      }
    {{~/components_radio_button}}
  {{else}}
    <div class="mdc-form-field">
      <label>You don't have any saved searches</label>
    </div>
  {{/with}}
{{/if ~}}

partials.event_search_results:
{{> banner_ad}}

{{> search_info paging=@root.data.search_data.detailed_results.pagination}}

<div class="mdc-layout-grid head">
  <div class="mdc-layout-grid__inner">
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-3-desktop mdc-layout-grid__cell--span-8-tablet mdc-layout-grid__cell--span-4-phone">
