partials.handle_404:
{{#if @root.data.primaryResponse.hits.hits.[0] ~}}
{{> @partial-block }}
{{else}}
<div class="mdc-layout-grid">
  <div class="mdc-layout-grid__inner">
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-8-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone content page-content">
      <h2>The page you requested could not be found</h2>
      <p>
        We recently relaunched our website and some page addresses may have changed.
      </p>
      {{#ifAll @root.data.supplimentaryResponses.not_found (compare @root.data.supplimentaryResponses.not_found.hits.total ">" 0) ~}}
      <h3>Did you mean?</h3>
      {{#each @root.data.supplimentaryResponses.not_found.hits.hits ~}}
      {{#compare @index "===" 0 ~}}
      {{>highlight_result}}
      {{else}}
      {{/compare ~}}
      {{/each ~}}
      {{/ifAll ~}}
    </div>
    <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone">
      <img src="{{~>asset_url url='images/404.png'~}}" class="responsive">
    </div>
  </div>
</div>
{{#ifAll @root.data.supplimentaryResponses.not_found (compare @root.data.supplimentaryResponses.not_found.hits.total ">" 1) ~}}
<hr />
<h3>Other relevant content</h3>
{{#each @root.data.supplimentaryResponses.not_found.hits.hits ~}}
{{#compare @index "===" 0 ~}}
{{else}}
{{>highlight_result}}
{{/compare ~}}
{{/each ~}}
{{else}}
<h3>Try searching for a topic</h3>
{{/ifAll ~}}

<script>
gtag('event', '404', {
  event_category: '{{name}}',
  event_label: '{{request.params.uri.pathname}}'
});
</script>
{{/if ~}}

partials.opportunity_notes:
{{#compare (trim (getProperty @root.data.profileInfo.notes Id)) "!==" "" ~}}
  <div class="search-result-article__notes">
    <h4 class="mdc-typography--headline7">
      Your notes&ensp;
      <a href="https://search.volunteerscotland.net/opportunity/{{Id}}/{{title_slug}}?utm_source=goodmoves&utm_campaign=search&utm_medium=website#notes" class="mdc-typography--body2">Edit</a>
    </h4>
    <div class="mdc-typography--body1">
      {{#if limit ~}}
        {{#compare @root.route.name "===" "notes" ~}}
          {{{markdown (getProperty @root.data.profileInfo.notes Id)}}}
        {{else}}
          <div style="max-height: {{limit}};" class="faded-overflow">
          {{{markdown (truncate (getProperty @root.data.profileInfo.notes Id) limit)}}}
          </div>
          <a href="https://search.volunteerscotland.net/opportunity/{{Id}}/{{title_slug}}?utm_source=goodmoves&utm_campaign=search&utm_medium=website#notes">Read more</a>
        {{/compare ~}}
      {{else}}
        {{{markdown (getProperty @root.data.profileInfo.notes Id)}}}
      {{/if ~}}
    </div>
  </div>
{{/compare ~}}

