},
{{/if ~}}
{{#if volunteersearch_types ~}}
{{>queries_terms field="volunteersearch_type_slugs" terms=volunteersearch_types}},
{{/if ~}}
{{#if activities ~}}
{{>queries_terms field="activities_slugs" terms=activities}},
{{/if ~}}
{{#if themes ~}}
{{>queries_terms field="themes_slugs" terms=themes}},
{{/if ~}}
{{#if services ~}}
{{>queries_terms field="volunteersearch_support_option_slugs" terms=services}},
{{/if ~}}
{{#if age_groups ~}}
{{>queries_terms field="volunteersearch_age_group_slugs" terms=age_groups}},
{{/if ~}}
{{#if availability ~}}
{{>queries_terms field="availability_slugs" terms=availability}},
{{/if ~}}
{{>queries_do_nothing}}

partials.queries_vacancy_search_filters:
{{#if working_patterns ~}}
  {{>queries_terms field="role_status_slug" terms=working_patterns}},
{{/if}}
{{#if sectors ~}}
  {{>queries_terms field="sectors_slugs" terms=sectors}},
{{/if ~}}
{{#if roles ~}}
  {{>queries_terms field="roles_slugs" terms=roles}},
{{/if ~}}
{{#if salary_min ~}}
  {{>queries_number_range field="salary_min" gte=salary_min}},
{{/if ~}}
{{#if salary_max ~}}
{{#compare salary_max "===" "0" ~}}
  {{>queries_term field="remuneration_type" term="Unpaid"}},
{{else}}
  {{>queries_number_range field="salary_min" lte=salary_max}},
{{/compare ~}}
{{/if ~}}
{{#ifAny regions location home_based various_locations ~}}
{
  "bool": {
    "should": [
      {{#if regions ~}}
        {
          "bool": {
            "should": [
              {{>queries_terms field="region_group_slug" terms=regions}},
              {{>queries_terms field="regions_scotland_slugs" terms=regions}}
            ],
            "minimum_should_match": 1
          }
        },
      {{/if ~}}
      {{#ifAll location lat lng distance ~}}
        {
          "geo_distance": {
            "distance": "{{default distance "8.04672"}}km",
            "geo_coords": {
              "lat": {{lat}},
              "lon": {{lng}}
            }
          }
        },
        {{#compare @root.route.name "!==" "vacancy_map" ~}}
          {
            "bool": {
              "should": [
                {
                  "term": {
                    "home_based": true
                  }
                },
                {
                  "term": {
                    "various_locations": true
                  }
                }
              ]
            }
          },
        {{else}}
          {{#if home_based ~}}
            {
              "term": {
                "home_based": true
              }
            },
          {{/if ~}}
          {{#if various_locations ~}}
            {
              "term": {
                "various_locations": true
              }
            },
          {{/if ~}}
        {{/compare ~}}
      {{else}}
        {{#if home_based ~}}
          {
            "term": {
              "home_based": true
            }
          },
        {{/if ~}}
        {{#if various_locations ~}}
          {
            "term": {
              "various_locations": true
            }
          },
        {{/if ~}}
      {{/ifAll ~}}
      {{>queries_do_nothing}}
    ],
    "minimum_should_match": {{#if distance}}2{{else}}{{#ifAny home_based various_locations regions}}2{{else}}1{{/ifAny}}{{/if}}
  }
},
{{/ifAny ~}}
{{>queries_vacancy_common_filters}}

