templates.sitemap:
{{~#*inline "url"~}}
  <url>
    <loc>{{loc}}</loc>
    {{#if lastmod~}}
    <lastmod>{{lastmod}}</lastmod>
    {{~/if}}
    {{#if changefreq~}}
    <changefreq>{{changefreq}}</changefreq>
    {{~/if}}
    {{#if priority~}}
    <priority>{{priority}}</priority>
    {{~/if}}
  </url>
{{~/inline~}}
<?xml version="1.0" encoding="UTF-8"?>
<urlset xmlns="http://www.sitemaps.org/schemas/sitemap/0.9">
  <!-- Static Routes -->
  {{> url
    loc=(concat "https://goodmoves.com/")
    lastmod=(momentFormat (moment "" "") "YYYY-MM-DD")
    changefreq="daily"
    priority="0.75"
  }}
  {{> url
    loc=(concat "https://goodmoves.com/sign-in")
    changefreq="yearly"
  }}
  {{> url
    loc=(concat "https://goodmoves.com/sign-up")
    changefreq="yearly"
  }}
  <!-- Districts -->
  {{#each @root.data.termsMaps.districts.slugsMap}}
    {{> url
      loc=(concat "https://goodmoves.com/jobs-in/" @key)
      lastmod=(momentFormat (moment "" "") "YYYY-MM-DD")
      changefreq="daily"
      priority="0.75"
    }}
    <!-- {{term}} Wards: -->
    {{#each wards}}
      {{> url
        loc=(concat "https://goodmoves.com/jobs-in/" @../key "/" @key)
        lastmod=(momentFormat (moment "" "") "YYYY-MM-DD")
        changefreq="daily"
        priority="0.75"
      }}
    {{/each}}
  {{/each}}
  <!-- Vacancies -->
  {{#each @root.data.supplimentaryResponses.aggs.hits.hits}}
    {{> url
      loc=(concat "https://goodmoves.com/vacancy/" _source.slug)
      lastmod=(momentFormat (moment _source.start_at "x") "YYYY-MM-DD")
    }}
  {{/each}}
  <!-- Organisations -->
  {{#each @root.data.supplimentaryResponses.aggs.aggregations.organisations.buckets}}
    {{> url
      loc=(concat "https://goodmoves.com/organisation/" key)
      lastmod=(momentFormat (moment last_updated.value "x") "YYYY-MM-DD")
      changefreq="weekly"
    }}
  {{/each}}
  <!-- CMS Pages -->
  {{#each @root.data.supplimentaryResponses.pages.hits.hits}}
    {{> url
      loc=(concat "https://goodmoves.com/" (replace _source.slug "_" "/"))
      lastmod=(momentFormat (moment _source.updated "x") "YYYY-MM-DD")
      changefreq="monthly"
    }}
  {{/each}}
</urlset>

templates.vacancies_dynamic-es-task:
{{#if @root.data.notifications.subscriptions.[0].[0]}}
{
  "name": "search_data",
  "taskModule": "elasticsearch",
  "renderer": "handlebars",
  "config": {
    "connectionStringTemplate": "{{{@root.context.metaData.elasticsearchUrl}}}",
    "elasticsearchConfig": {
      "apiVersion": "5.6"
    },
    "queryTemplates": [
      {{#each @root.data.notifications.subscriptions.[0]}}
      {
        "name": {{{stringify (regexReplace email "\." "gi" "_")}}},
        "index": "{$../../../elasticsearch-index.txt}",
        "type": "goodmoves-vacancy",
        "template": ">context.metaData.handlebars.queries.vacancies_search-all",
        "metaData": {{{stringify this}}}
      }{{#unless @last}},{{/unless}}
      {{/each}}
    ]
  }
}
{{else}}
{
  "name": "search_data",
  "taskModule": "render",
  "renderer": "handlebars",
  "config": {
    "output": "data",
    "template": "{ \"status\": \"No more subscriptions to process\" }",
    "parseJson": true
  }
}
{{/if}}

templates.vacancies_email:
[
{{#if @root.data.search_data}}
  {{#each @root.data.notifications.subscriptions.[0]}}
    {
      "from": {{{stringify @root.route.metaData.from}}},
      "to": {{{stringify email}}},
      "subject": {{{stringify @root.route.metaData.subject}}},
      "o:tag": {{{stringify @root.context.metaData.emailCampaignName}}},
      "tag": {{{stringify @root.context.metaData.emailCampaignName}}},
      "id": {{{stringify @root.context.metaData.emailCampaignName}}},
      "html": {{#blockStringify this}}
        <div>
          <h1>Goodmoves Job Alert!</h1>
          <p>
            <strong>Hey there {{email}}!</strong>
          <p>
          {{#with (getProperty @root.data.search_data (regexReplace email "\." "gi" "_"))}}
            {{#if hits.hits.[0]}}
              <p>
                Here are jobs since the (Need to get date last sent here) that match your saved search preferences 
              </p>
              {{#each hits.hits}}
              <h2>
                <a href="https://goodmoves.com/vacancy/{{_source.slug}}">
                {{_source.title}}
                </a>
              </h2>
              <ul>
                <li>
                  <strong>Organisation:</strong>
                  <a href="https://goodmoves.com/organisation/{{_source.organisation.slug}}">
                    {{_source.organisation.name}}
                  </a>
                </li>
                <li>
                  <strong>Salary:</strong>
                  {{_source.salary}}
                </li>
                <li>
                  <strong>Location:</strong>
                  {{_source.location}}
                </li>
              </ul>
              <div>
                {{{_source.description}}}
              </div>
              {{/each}}
            {{else}}
              <p>We have nothing for you this week</p>
            {{/if}}
          {{else}}
            <p>We have nothing for you this week... aaand something seems to have gone wrong!</p>
          {{/with}}
        </div>
      {{/blockStringify}}
    }{{#unless @last}},{{/unless}}
  {{/each}}
{{/if}}
]

templates.vacancies_map:
{{#*inline "marker"}}
<marker style="display:none;" data-map="fullMap" data-lat="{{lat}}" data-lng="{{lng}}" data-title="{{source.title}}">
  {{>vacancy_map source postcode=(itemAt (arrayify source.postcodes) index)}}
</marker>
{{/inline}}

{{#if @root.data.search_data.map_results.pagination.totalResults ~}}
<div id="detailed-results-container" class="container-content container-map">
  <div id="map-full" data-map-name="fullMap" data-map-clustered-options='{
    {{#ifAll @root.request.params.query.location @root.request.params.query.lat @root.request.params.query.lng ~}}
      "circle": {
        "lat": {{@root.request.params.query.lat}},
        "lng": {{@root.request.params.query.lng}},
        "radius": {{default @root.request.params.query.distance 0}},
        "color": "#9cd986"
      }
    {{/ifAll ~}}
    }'>
    {{#each @root.data.search_data.map_results.hits.hits ~}}
      {{#if _source.geo_coords ~}}
        {{#each (arrayify _source.geo_coords) ~}}
          {{#if @root.request.params.query.regions ~}}
            {{!-- {{#switch (getProperty (itemAt ../_source.geo_info @index) "nhs_ha") ~}}
            {{#case "Lothian" ~}}
            {{#if (contains @root.request.params.query.regions "edinburgh-lothian") ~}} --}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{!-- {{/if ~}}
            {{/case ~}}
            {{#case "Greater Glasgow and Clyde" ~}}
            {{#if (contains @root.request.params.query.regions "glasgow-clyde") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Tayside" ~}}
            {{#if (contains @root.request.params.query.regions "tayside") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Forth Valley" ~}}
            {{#if (contains @root.request.params.query.regions "central-scotland") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Western Isles" ~}}
            {{#if (contains @root.request.params.query.regions "na-h-eileanan-siar") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Fife" ~}}
            {{#if (contains @root.request.params.query.regions "fife") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Grampian" ~}}
            {{#if (contains @root.request.params.query.regions "grampian") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Highland" ~}}
            {{#if (contains @root.request.params.query.regions "highlands") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Lanarkshire" ~}}
            {{#if (contains @root.request.params.query.regions "glasgow-clyde") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Orkney" ~}}
            {{#if (contains @root.request.params.query.regions "northern-isles") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Shetland" ~}}
            {{#if (contains @root.request.params.query.regions "northern-isles") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{#case "Argyll & Bute" ~}}
            {{#if (contains @root.request.params.query.regions "argyll-bute") ~}}
            {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
            {{/case ~}}
            {{/switch ~}} --}}
          {{else}}
            {{#if @root.request.params.query.distance ~}}
              {{#compare (itemAt ../fields.distance @index) "<" @root.request.params.query.distance ~}}
                {{> marker lat=lat lng=lon source=../_source index=@index}}
              {{/compare ~}}
            {{else}}
              {{> marker lat=lat lng=lon source=../_source index=@index}}
            {{/if ~}}
          {{/if ~}}
        {{/each ~}}
      {{/if ~}}
    {{/each ~}}
  </div>
</div>
{{else}}
<div id="detailed-results-container" class="container-content content-primary-search">
  <div class="search-info mdc-elevation--z1">
    No results could be found, please try a different search query
  </div>
</div>
{{/if ~}}

