{
  "size": 200,
  "_source": [
    "Id",
    "slug",
    "top_job",
    "description",
    "title",
    "title_slug",
    "geo_coords",
    "home_based",
    "various_locations",
    "location",
    "salary",
    "salary_currency",
    "role_status_list",
    "active",
    "closing_date",
    "start_at",
    "end_at",
    "organisation.slug",
    "organisation.name",
    "organisation.id",
    "organisation.name_slug",
    "organisation.has_logo",
    "organisation.logo_url",
    "rendered.goodmoves_rss"
  ],
  "query": {
    "function_score": {
      "query": {
        "bool": {
          "must": [
            {{#each (split templateMetaData.querystrings "&&")}}
              {{#with (querystring this)}}
              {
                "bool": {
                  "filter": [
                    {{>queries_vacancy_common_filters_open this}},
                    {{>queries_vacancy_search_filters this}}
                  ],
                  "must": [
                    {{>queries_vacancy_search_must this}}
                  ]
                }
                {{/with}}
              }{{#unless @last}},{{/unless}}
            {{/each}}
          ]
        }
      },
      "boost": 1,
      "functions": [
        {
          "filter": {
            "match": {
              "top_job": true
            }
          },
          "weight": 5
        }
      ],
      "max_boost": 10,
      "score_mode": "max",
      "boost_mode": "multiply",
      "min_score" : 0
    }
  },
  "sort": [
    {
      "start_at": "asc"
    }
  ]
}

queries.vacancies_search-map:
{
  "size": 1000,
  "_source": [
    "Id",
    "title",
    "title_slug",
    "organisation.name",
    "organisation.logo",
    "organisation.id",
    "organisation.name_slug",
    "geo_coords",
    "geo_info",
    "regions_scotland_slugs",
    "active",
    "closing_date",
    "start_at",
    "end_at",
    "home_based",
    "various_locations",
    "salary",
    "role_status_list",
    "location",
    "postcodes",
    "slug"
  ],
  "query": {
    "bool": {
      "filter": [
        {{>queries_vacancy_common_filters_open @root.request.params.query}},
        {{>queries_vacancy_search_filters @root.request.params.query}}
      ],
      "must": [
        {{>queries_vacancy_search_must @root.request.params.query}}
      ],
      "must_not": [
        {
          "term": {
            "home_based": true
          }
        },
        {
          "term": {
            "various_locations": true
          }
        }
      ]
    }
  },
  {{#ifAll @root.request.params.query.location @root.request.params.query.lat @root.request.params.query.lng ~}}
  "script_fields": {
    "distance": {
      "script": {
        "inline": "def l = new ArrayList(); for (int i=0; i<doc['geo_coords'].values.length; i++) { double dist = Double.MAX_VALUE; double TO_METERS = 6371008.7714D; double TO_RADIANS = Math.PI / 180D; double doc_lat, doc_lon, x1, x2, h1, h2, h, cdist; doc_lat = params._source['geo_coords'][i].lat; doc_lon = params._source['geo_coords'][i].lon; x1 = doc_lat * TO_RADIANS; x2 = params.lat * TO_RADIANS; h1 = 1 - Math.cos(x1 - x2); h2 = 1 - Math.cos((doc_lon - params.lon) * TO_RADIANS); h = h1 + Math.cos(x1) * Math.cos(x2) * h2; cdist = TO_METERS * 2 * Math.asin(Math.min(1, Math.sqrt(h * 0.5))); dist = Math.min(dist, cdist); l.add(dist * 0.001) } return l;",
        "lang": "painless",
        "params": {
          "lat": {{@root.request.params.query.lat}},
          "lon": {{@root.request.params.query.lng}}
        }
      }
    }
  },
  {{/ifAll ~}}
  "sort": [
    {{#ifAll @root.request.params.query.location @root.request.params.query.lat @root.request.params.query.lng ~}}
      {
        "_geo_distance": {
          "geo_coords": {
            "lat": {{@root.request.params.query.lat}},
            "lon": {{@root.request.params.query.lng}}
          },
          "order": "asc",
          "unit": "km",
          "mode": "min",
          "distance_type": "arc"
        }
      }
    {{else}}
      {
        "closing_date": "asc"
      }
    {{/ifAll ~}}
  ]
}
